import { defineComponent, getCurrentInstance, reactive } from 'vue';
import XEUtils from 'xe-utils';
export default {
  data: function data() {
    return {
      // submenu: {
      //   submenuHide: false,
      //   submenuShow: true
      // },
      getTableData: []
    };
  },
  mounted: function mounted() {
    // 页面加载时触发函数
    console.log("获取员工列表");
    this.getUserList();
  },
  // setup表示允许在组件内部定义响应式状态和事件处理逻辑
  setup: function setup() {
    console.log("setup");
    var demo1 = reactive({
      filterName: '',
      list: [],
      tableData: []
    });
    demo1.tableData = getCurrentInstance();
    var searchEvent1 = function searchEvent1() {
      var filterName = XEUtils.toValueString(demo1.filterName).trim().toLowerCase();
      if (filterName) {
        var filterRE = new RegExp(filterName, 'gi');
        var searchProps = ['nickName', 'username'];
        var rest = demo1.tableData.filter(function (item) {
          return searchProps.some(function (key) {
            return XEUtils.toValueString(item[key]).toLowerCase().indexOf(filterName) > -1;
          });
        });
        demo1.list = rest.map(function (row) {
          var item = Object.assign({}, row);
          searchProps.forEach(function (key) {
            item[key] = XEUtils.toValueString(item[key]).replace(filterRE, function (match) {
              return "<span class=\"keyword-lighten\">".concat(match, "</span>");
            });
          });
          return item;
        });
      } else {
        demo1.list = demo1.tableData;
      }
    };
    searchEvent1();
    return {
      demo1: demo1,
      searchEvent1: searchEvent1
    };
  },
  methods: {
    getUserList: function getUserList() {
      var _this = this;
      this.$store.dispatch('GetUserList').then(function (res) {
        // 拿到结果
        var statusCode = res.data.statusCode;
        _this.getTableData = res.data.data;
        // 判断结果
        if (statusCode == 200) {
          console.log("获取员工列表成功");
        } else {
          console.log("获取员工列表成功");
        }
      }).catch(function () {
        _this.loading = false;
      });
    }
  }
};
